INSERT INTO `sl_schedule` VALUES ('15', 'Day 1', '开营培训', '<span style=\"color: rgb(50, 50, 50); font-family: Arial, \'Microsoft YaHei\'; font-size: 14px; line-height: 22px;\">到达大理，开营培训，为小坚果们梳理课程的全貌。</span><br />', null, '1', '7', '50');
INSERT INTO `sl_schedule` VALUES ('16', 'Day 2', '苍山+洱海+坝子', '<span style=\"color: rgb(50, 50, 50); font-family: Arial, \'Microsoft YaHei\'; font-size: 14px; line-height: 22px;\">徒步苍山玉带路，俯瞰大理坝子，了解安居乐业地理条件，学习生物多样性保护，挑战体力。沿途讲解并体验苍山独特的地理、地质、植物和风光：</span><br style=\"color: rgb(50, 50, 50); font-family: Arial, \'Microsoft YaHei\'; font-size: 14px; line-height: 22px;\" /><span style=\"color: rgb(50, 50, 50); font-family: Arial, \'Microsoft YaHei\'; font-size: 14px; line-height: 22px;\">得天独厚的地理条件：疆土（苍山+洱海+坝子）</span><br style=\"color: rgb(50, 50, 50); font-family: Arial, \'Microsoft YaHei\'; font-size: 14px; line-height: 22px;\" /><span style=\"color: rgb(50, 50, 50); font-family: Arial, \'Microsoft YaHei\'; font-size: 14px; line-height: 22px;\">大理独特的气候与天气的形成</span><br style=\"color: rgb(50, 50, 50); font-family: Arial, \'Microsoft YaHei\'; font-size: 14px; line-height: 22px;\" /><span style=\"color: rgb(50, 50, 50); font-family: Arial, \'Microsoft YaHei\'; font-size: 14px; line-height: 22px;\">人如何选择居住地</span><br style=\"color: rgb(50, 50, 50); font-family: Arial, \'Microsoft YaHei\'; font-size: 14px; line-height: 22px;\" /><span style=\"color: rgb(50, 50, 50); font-family: Arial, \'Microsoft YaHei\'; font-size: 14px; line-height: 22px;\">如何利用地理优势防御侵略</span><br />', null, '1', '7', '60');
INSERT INTO `sl_schedule` VALUES ('17', 'Day 3', '古城、古村民俗体验', '<span style=\"color: rgb(50, 50, 50); font-family: Arial, \'Microsoft YaHei\'; font-size: 14px; line-height: 22px;\">走访不同级别的古城、古镇和古村，理解其不同的城镇格局。走访当地人的民俗和价值观，了解文化多样性的保护。</span><br style=\"color: rgb(50, 50, 50); font-family: Arial, \'Microsoft YaHei\'; font-size: 14px; line-height: 22px;\" /><span style=\"color: rgb(50, 50, 50); font-family: Arial, \'Microsoft YaHei\'; font-size: 14px; line-height: 22px;\">探访蟠曲村，古村建筑绘画临摹</span><br style=\"color: rgb(50, 50, 50); font-family: Arial, \'Microsoft YaHei\'; font-size: 14px; line-height: 22px;\" /><span style=\"color: rgb(50, 50, 50); font-family: Arial, \'Microsoft YaHei\'; font-size: 14px; line-height: 22px;\">探访喜洲镇，参观建筑、收集诗词名句、对联；</span><br style=\"color: rgb(50, 50, 50); font-family: Arial, \'Microsoft YaHei\'; font-size: 14px; line-height: 22px;\" /><span style=\"color: rgb(50, 50, 50); font-family: Arial, \'Microsoft YaHei\'; font-size: 14px; line-height: 22px;\">民俗体验：品白族三道茶，喜洲粑粑，周城扎染参观</span><br style=\"color: rgb(50, 50, 50); font-family: Arial, \'Microsoft YaHei\'; font-size: 14px; line-height: 22px;\" />', null, '1', '7', '70');
INSERT INTO `sl_schedule` VALUES ('18', 'Day 4', '苍山十八溪', '<span style=\"color: rgb(50, 50, 50); font-family: Arial, \'Microsoft YaHei\'; font-size: 14px; line-height: 22px;\">徒手溯溪，考察并体验苍山十八溪的现状。了解农田灌溉系统，农作物配置，；理解大理人为安居乐业而泽水而居的必要选择。</span><br />', null, '1', '7', '80');
INSERT INTO `sl_schedule` VALUES ('19', 'Day 5', '洱海骑行', '<span style=\"color: rgb(50, 50, 50); font-family: Arial, \'Microsoft YaHei\'; font-size: 14px; line-height: 22px;\">洱海骑行，考察洱海现状，了解洱海水资源的利用和保护；晚上去洋人街采访并帮忙摆摊，了解新移民的生活方式和心态。</span><br style=\"color: rgb(50, 50, 50); font-family: Arial, \'Microsoft YaHei\'; font-size: 14px; line-height: 22px;\" />', null, '1', '7', '90');
