INSERT INTO `sl_schedule` VALUES ('21', 'Day1', '出发', '<span style=\"color: rgb(50, 50, 50); font-family: Arial, \'Microsoft YaHei\'; font-size: 14px; line-height: 22px;\">北京-新加坡</span><br style=\"color: rgb(50, 50, 50); font-family: Arial, \'Microsoft YaHei\'; font-size: 14px; line-height: 22px;\" /><p><span style=\"color: rgb(50, 50, 50); font-family: Arial, \'Microsoft YaHei\'; font-size: 14px; line-height: 22px;\">（参考航班：SQ807 16：35--22：55），搭乘新航世界上最大的民用客机A380</span></p><p><span style=\"color: rgb(50, 50, 50); font-family: Arial, \'Microsoft YaHei\'; font-size: 14px; line-height: 22px;\"><img src=\"/upfiles/images/201507/0214358204356109.jpg\" alt=\"\" /><br /></span></p>', null, '2', '4', '10');
INSERT INTO `sl_schedule` VALUES ('22', 'Day2', '参观学府', '<p><span style=\"color: rgb(50, 50, 50); font-family: Arial, \'Microsoft YaHei\'; font-size: 14px; line-height: 22px;\">深入体验参观新加坡南洋理工大学、新加坡国立大学，领略知名学府的氛围。游玩全球著名娱乐名胜环球影城，穿越梦想时空。&nbsp;</span></p><p><span style=\"color: rgb(50, 50, 50); font-family: Arial, \'Microsoft YaHei\'; font-size: 14px; line-height: 22px;\"><img src=\"/upfiles/images/201507/0214358204688108.jpg\" alt=\"\" /><br /></span></p>', null, '2', '4', '20');
INSERT INTO `sl_schedule` VALUES ('23', 'Day3', '协作课程', '<p><span style=\"color: rgb(50, 50, 50); font-family: Arial, \'Microsoft YaHei\'; font-size: 14px; line-height: 22px;\">新加坡国立大学国际青少年领袖领导力及团队协作课程，夜游新加坡河，观看大型音乐喷泉表演，领略无敌夜景。SEL教学模式英文授课，体会国际领导力和青少年领袖的培养，切身感受国际一流教育理念和教学方法。&nbsp;</span></p><p><span style=\"color: rgb(50, 50, 50); font-family: Arial, \'Microsoft YaHei\'; font-size: 14px; line-height: 22px;\"><img src=\"/upfiles/images/201507/0214358205018801.jpg\" alt=\"\" /><br /></span></p>', null, '2', '4', '30');
INSERT INTO `sl_schedule` VALUES ('24', 'Day4', '文化之旅', '<p><span style=\"color: rgb(50, 50, 50); font-family: Arial, \'Microsoft YaHei\'; font-size: 14px; line-height: 22px;\">Day4新加坡南洋理工大学，东西方文化探索社交技巧国际课程。走向国际的第一步，全面提升东西方文化及提升社交素养。包含上午茶、下午茶和西餐正餐的体验礼仪培训，由国际文化大使和礼仪大使授课。</span></p><p><span style=\"color: rgb(50, 50, 50); font-family: Arial, \'Microsoft YaHei\'; font-size: 14px; line-height: 22px;\"><img src=\"/upfiles/images/201507/0214358205288606.jpg\" alt=\"\" /><br /></span></p>', null, '2', '4', '40');
