INSERT INTO `sl_schedule` VALUES ('40', 'Day1', '帆船', '<p>上午 &nbsp;帆船，陆地演习，迎风换舷</p><p>午餐</p><p>下午 转向练习，帆船组装</p><p>晚餐</p><p>晚上 沙滩游戏</p><p><br /></p>', null, '1', '22', '260');
