INSERT INTO `sl_schedule` VALUES ('42', 'Day 3', '帆板', '<p>上午 &nbsp;帆板 迎风转向&amp;逆风转向</p><p>午餐</p><p>下午 皮划艇</p><p>晚餐</p><p>晚上 沙滩排球</p><div><br /></div>', null, '1', '22', '280');
