INSERT INTO `sl_schedule` VALUES ('45', 'Day 5', '激情水世界', '<p><span style=\"color: rgb(50, 50, 50); font-family: Arial, \'Microsoft YaHei\'; font-size: 14px; line-height: 22px;\">圣陶沙水上探险乐园、SEA海洋馆、海事博物馆。这将是令您终生难忘的水上狂欢日，还有无数的好玩的特色店，全世界最大的糖果店一定会让孩子们大开眼界！</span></p><p><span style=\"color: rgb(50, 50, 50); font-family: Arial, \'Microsoft YaHei\'; font-size: 14px; line-height: 22px;\"><img src=\"/upfiles/images/201507/0214358205688849.jpg\" alt=\"\" /><br /></span></p>', null, '2', '4', '50');
INSERT INTO `sl_schedule` VALUES ('46', 'Day 6', '文化大碰撞', '<span style=\"color: rgb(50, 50, 50); font-family: Arial, \'Microsoft YaHei\'; font-size: 14px; line-height: 22px;\">&nbsp;不同族群的Fusion. 小印度, 甘榜格南, 牛车水。</span><br style=\"color: rgb(50, 50, 50); font-family: Arial, \'Microsoft YaHei\'; font-size: 14px; line-height: 22px;\" /><span style=\"color: rgb(50, 50, 50); font-family: Arial, \'Microsoft YaHei\'; font-size: 14px; line-height: 22px;\">新生水世界参观，见证全球最清洁的新生水的净化过程，全英文讲解，这是我们学习科技英语的好机会。游览世界上最大的摩天轮，新加坡之眼，繁华美景一览无余。</span><br />', null, '2', '4', '60');
INSERT INTO `sl_schedule` VALUES ('47', 'Day7', '市内观光', '<p><span style=\"color: rgb(50, 50, 50); font-family: Arial, \'Microsoft YaHei\'; font-size: 14px; line-height: 22px;\">参观各处名胜，仿佛从英国殖民地穿越到繁华的都市。让孩子了解到新加坡的历史、文化和文明。新加坡科学馆，飞禽公园。</span></p><p><span style=\"color: rgb(50, 50, 50); font-family: Arial, \'Microsoft YaHei\'; font-size: 14px; line-height: 22px;\"><img src=\"/upfiles/images/201507/0214358206697754.jpg\" alt=\"\" /><br /></span></p>', null, '2', '4', '70');
